import React, {useState} from 'react'
import Validation from '../forms/validation'
import Alert from '../alerts'
import {axiosInstance} from "../../util/axios";
import {login} from "../../util/cookies";
import {useRouter} from "next/router";

export default function AccountSettings(props,{message = null}) {
  const [data, setData] = useState(null)
  const {user} = props;

  console.log(user,"--------user---------")
  const router = useRouter();

  const onSubmit = async (values) => {
    await axiosInstance.put(`/user/${user.id}`, values).then(response => {
      router.push(`/view/${response.data.id}`);
    })

  };
  let items = [
    {
      label: 'User name',
      error: {required: 'Please enter a valid first name'},
      name: 'first-name',
      type: 'text',
      placeholder: user.newUser.userName
    },
    {
      label: 'First name',
      error: {required: 'Please enter a valid first name'},
      name: 'first-name',
      type: 'text',
      placeholder: user.newUser.firstName
    },
    {
      label: 'Last name',
      error: {required: 'Please enter a valid last name'},
      name: 'last-name',
      type: 'text',
      placeholder: user.newUser.lastName
    },
    {
      label: 'Email address',
      error: {required: 'Please enter a valid email address'},
      name: 'email',
      type: 'email',
      placeholder: user.newUser.email
    },
    {
      label: 'Bio',
      error: {required: 'Please enter a bio'},
      name: 'bio',
      type: 'text',
      placeholder: user.newUser.bio
    },

  ]
  return (
    <>
      <div className="flex flex-col">
        {data && message && (
          <div className="w-full mb-4">
            <Alert
              color="bg-transparent border-green-500 text-green-500"
              borderLeft
              raised>
              {message}
            </Alert>
          </div>
        )}
        <Validation items={items} onSubmit={onSubmit} />
      </div>
    </>
  )
}
